let voltaje_caudal = msg.payload.A0.voltage;
let voltaje_nivel = msg.payload.A1.voltage;

// Convertir voltaje a profundidad
/* 
    V = 4mA * 150ohm = 0.6V
    V = 20mA * 150ohm = 3.0V

    0.6V -> 0m
      x  -> h
    3.0V -> h_max (max profundidad) m
    (h_max - 0)/(3 - 0.6) = (h - 0)/(x - 0.6)
    h = h_max*(x - 0.6)/2.4
*/
const h_max = 2;      // Profundidad maxima detectada por sensor m
const w = 2.8;        // Ancho canal m
const S = 0.001;      // Pendiente canal m
const n = 0.0125;     // Rugosidad pared canal

// Calcular profundidad en sensor caudal.
let h_caudal = h_max*(voltaje_caudal - 0.6)/2.4;
if(h_caudal < 0) h_caudal = 0;

// Calcular Area
let A = w*h_caudal;

// Velocidad promedio agua
let R = A/(2*h_caudal+w);
let V = R**(2/3)*S**(1/2)/n;

// Caudal canal
let Q = A*V;

// Calcular profundidad en sensor nivel.
let h_nivel = h_max*(voltaje_nivel - 0.6)/2.4;
if(h_nivel < 0) h_nivel = 0;

msg.payload = {
  voltaje_caudal: voltaje_caudal,
  profundidad_caudal: h_caudal,
  velocidad: V,
  caudal: Q,
  voltaje_nivel: voltaje_nivel,
  profundidad_nivel: h_nivel};

return msg;

